import { useEffect, useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

type Location = {
  latitude: string;
  longitude: string;
};
interface IMyOption {
  label: string;
  value: Location;
}
interface ICity {
  name: string;
  countryCode: string;
  latitude: string;
  longitude: string;
}

export const Search = () => {
  const [search, setSearch] = useState<string>('');
  // const [latitude, setLatitude] = useState<string[]>([]);
  // const [longitude, setLongitude] = useState<string[]>([]);
  const [location, setLocation] = useState<Location>();

  useEffect(() => {
    console.log(search);
    console.log(location);
  }, [search, location]);

  const handleChange = (optionData: IMyOption) => {
    setSearch(optionData.label);
    setLocation({
      latitude: optionData.value.latitude,
      longitude: optionData.value.longitude,
    });
    return;
  };

  const loadOptions = (inputText: string) => {
    return fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputText}`,
      options,
    )
      .then(response => response.json())
      .then(response => {
        return {
          options: response.data.map((city: ICity) => {
            return {
              label: `${city.name} ${city.countryCode}`,
              value: { latitude: city.latitude, longitude: city.longitude },
            };
          }),
        };
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        onChange={handleChange}
        loadOptions={loadOptions}
      />
    </div>
  );
};
