import { apiOptions } from '../../../../axios/geoApiOptions';
interface ICity {
  name: string;
  countryCode: string;
  latitude: string;
  longitude: string;
}
const loadOptions2 = async (inputText: string) => {
  await fetch(
    `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputText}`,
    apiOptions,
  );
};

const loadOptions = new Promise<void>(resolve => {
  fetch(
    `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${'Paris'}`,
    apiOptions,
  )
    .then(response => response.json())
    .then(response => ({
      options: response.data.map((city: ICity) => ({
        label: `${city.name} ${city.countryCode}`,
        value: { latitude: city.latitude, longitude: city.longitude },
      })),
    }))
    .catch(err => console.error(err));
  resolve();
});

export default loadOptions;
