import { useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

interface Props {}

type City = { label: String; value: String };

export const Search = (onSearchChange: Props) => {
  const [search, setSearch] = useState('');

  const handleChange = (optionData: string) => {
    setSearch(optionData);
  };

  const loadOptions = (inputValue: string) => {
    return fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=10000&namePrefix=${inputValue}`,
      options,
    )
      .then(response => response.json())
      .then(response => {
        return {
          options: response.data.map((city: City) => {
            return {
              value: `${city.latitude} ${city.longitude}`,
              label: `${city.name} ${city.countryCode}`,
            };
          }),
        };
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        // @ts-ignore
        onChange={handleChange}
        // @ts-ignore
        loadOptions={loadOptions}
      />
    </div>
  );
};
