import axios from 'axios';

const api = axios.create({ baseURL: process.env.WEATHER_APP_API_URL });
console.dir('api', api);
api.interceptors.request.use(config => {
  config.url =
    config.url + 'cities?' + '&appid=' + process.env.WEATHER_APP_API_KEY;
  return config;
});

export default api;
