import axios from 'axios';

const api = axios.create({ baseURL: 'wft-geo-db.p.rapidapi.com' });

api.interceptors.request.use(config => {
  config.url =
    config.url + '&units=metric' + '&appid=' + process.env.WEATHER_APP_API_KEY;
  return config;
});

export default api;
