import { useEffect, useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import loadOptions from './loadOptions';

import { useCustomDispatch } from 'hooks/store';
import { SingleValue } from 'react-select';
import { fetchCurrentWeather } from 'store/thunks/fetchCurrentWeather';
import { ICoordinates, IMyOption } from 'store/types/types';

export const Search = () => {
  const [searchData, onSearchData] = useState<SingleValue<IMyOption>>({
    label: 'London',
    value: {
      latitude: -33.6485,
      longitude: 150.736,
    },
  });
  const [coordinates, setCoordinates] = useState<ICoordinates>();

  const dispatch = useCustomDispatch();

  useEffect(() => {
    dispatch(fetchCurrentWeather(coordinates));
    setCoordinates(searchData.value);
    // debugger;
    console.log('searchData', coordinates);
  }, [searchData]);

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={searchData}
        onChange={onSearchData}
        loadOptions={loadOptions}
      />
    </div>
  );
};
