import { SingleValue } from 'react-select';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

type City = {
  latitude: string;
  longitude: string;
  name: string;
  countryCode: string;
};
export const Search = () => {
  const [search, setSearch] =
    useState<React.Dispatch<React.SetStateAction<City>>>();

  const handleChange = (optionData?: SingleValue<City>) => {
    // console.log(optionData?.label);
    // console.log(optionData?.value);
    // setSearch(optionData);
    console.log(optionData?.countryCode);
  };

  const loadOptions = (inputValue: string) => {
    return fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputValue}`,
      options,
    )
      .then(response => response.json())
      .then(response => {
        return {
          options: response.data.map((city: City) => {
            return {
              latitude: `${city.latitude}`,
              longitude: ` ${city.longitude}`,
              name: `${city.name} `,
              countryCode: `${city.countryCode}`,
            };
          }),
        };
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        onChange={handleChange}
        loadOptions={loadOptions}
      />
    </div>
  );
};
