import { useEffect, useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';

type Location = {
  latitude: string;
  longitude: string;
};
interface IMyOption {
  label: string;
  value: Location;
}
interface ICity {
  name: string;
  countryCode: string;
  latitude: string;
  longitude: string;
}

export const Search = () => {
  // const [search, setSearch] = useState<string | null>(null);
  // const [location, setLocation] = useState<Location>();
  const [searchData, onSearchData] = useState<IMyOption | null>(null);

  useEffect(() => {
    // console.log(search);
    console.log(searchData);
  }, [searchData]);
  // Type '(optionData: IMyOption) => void' is not assignable to type '(newValue: SingleValue<string>, actionMeta: ActionMeta<string>) => void'.
  // const handleChange = (optionData: IMyOption) => {
  //   setSearch(optionData.label);
  //   setLocation({
  //     latitude: optionData.value.latitude,
  //     longitude: optionData.value.longitude,
  //   });
  //   return;
  // };

  const options: any = [];
  for (let i = 0; i < 50; ++i) {
    options.push({
      value: i + 1,
      label: `Option ${i + 1}`,
    });
  }

  const sleep = (ms: any) =>
    new Promise<void>(resolve => {
      setTimeout(() => {
        resolve();
      }, ms);
    });

  const loadOptions = async (search: string, prevOptions: any) => {
    await sleep(1000);

    let filteredOptions;
    if (!search) {
      filteredOptions = options;
    } else {
      const searchLower = search.toLowerCase();

      filteredOptions = options.filter(({ label }) =>
        label.toLowerCase().includes(searchLower),
      );
    }

    const hasMore = filteredOptions.length > prevOptions.length + 10;
    const slicedOptions = filteredOptions.slice(
      prevOptions.length,
      prevOptions.length + 10,
    );

    return {
      options: slicedOptions,
      hasMore,
    };
  };

  const shouldLoadMore = (
    scrollHeight: number,
    clientHeight: number,
    scrollTop: number,
  ) => {
    const bottomBorder = (scrollHeight - clientHeight) / 2;
    return bottomBorder < scrollTop;
  };

  // const loadOptions = async (search: string, prevOptions) => {
  //   return await fetch(
  //     `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${search}`,
  //     options,
  //   )
  //     .then(response => response.json())
  //     .then(response => ({
  //       options: response.data.map((city: ICity) => ({
  //         label: `${city.name} ${city.countryCode}`,
  //         value: { latitude: city.latitude, longitude: city.longitude },
  //       })),
  //     }))
  //     .catch(err => console.error(err));
  // };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={searchData}
        shouldLoadMore={shouldLoadMore}
        onChange={onSearchData}
        loadOptions={loadOptions}
      />
    </div>
  );
};
