import { useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

interface ILoadOptioons {
  options: [];
  hasMore: boolean;
  additional?: any;
}

type MyOption = { label: string; value: string };

export const Search = () => {
  const [search, setSearch] = useState<MyOption>();

  const handleChange = (optionData: MyOption) => {
    setSearch(optionData);
    console.log(optionData.label);
  };

  const loadOptions = (inputValue: ILoadOptioons) => {
    return fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputValue}`,
      options,
    )
      .then(response => response.json())
      .then(response => {
        return {
          options: response.data.map((city: any) => {
            return {
              value: `${city.latitude} ${city.longitude}`,
              label: `${city.name} ${city.countryCode}`,
            };
          }),
        };
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        onChange={handleChange}
        loadOptions={loadOptions}
      />
    </div>
  );
};
