import { useEffect, useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

interface IMyOption {
  label: string;
  value: string;
}
interface ICity {
  name: string;
  countryCode: string;
  latitude: string;
  longitude: string;
}

export const Search = () => {
  const [search, setSearch] = useState<string>('');
  const [lacation, setlacation] = useState<string>('');

  useEffect(() => {
    console.log(search);
  }, [search]);

  const handleChange = (optionData: IMyOption) => {
    setSearch(optionData.label);
    console.log(optionData.value);
    console.log(optionData.label);
  };
  // const loadOptions = (
  //     inputText: string,
  //     callback: (options: OptionTypeBase[]) => void
  // ): void =>
  const loadOptions = (
    inputText: string,
    callback: (options: OptionTypeBase[]) => void,
  ): void => {
    return fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputText}`,
      options,
    )
      .then(response => response.json())
      .then(response => {
        return {
          options: response.data.map((city: ICity) => {
            return {
              label: `${city.name} ${city.countryCode}`,
              value: `${city.latitude} ${city.longitude}`,
            };
          }),
        };
      })
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        // @ts-ignore
        onChange={handleChange}
        // @ts-ignore
        loadOptions={loadOptions}
      />
    </div>
  );
};
