import axios from 'axios';
import { WEATHER_APP_API_URL } from '../../.tokens';

const api = axios.create({
  baseURL: WEATHER_APP_API_URL,
});

api.interceptors.request.use(config => {
  config.url = config.url + '&units=metric' + '&appid=';
  return config;
});
export default api;
