import { useEffect, useState } from 'react';
import { AsyncPaginate } from 'react-select-async-paginate';
import { options } from '../../../../axios/geoApiOptions';

type Location = {
  latitude: string;
  longitude: string;
};
interface IMyOption {
  label: string;
  value: Location;
}
interface ICity {
  name: string;
  countryCode: string;
  latitude: string;
  longitude: string;
}

export const Search = () => {
  const [search, setSearch] = useState<string>();
  const [location, setLocation] = useState<Location>();

  useEffect(() => {
    console.log(search);
    console.log(location);
  }, [search, location]);
  // Type '(optionData: IMyOption) => void' is not assignable to type '(newValue: SingleValue<string>, actionMeta: ActionMeta<string>) => void'.
  const handleChange = (optionData: IMyOption) => {
    setSearch(optionData.label);
    setLocation({
      latitude: optionData.value.latitude,
      longitude: optionData.value.longitude,
    });
    return;
  };
  const shouldLoadMore = (scrollHeight, clientHeight, scrollTop) => {
    const bottomBorder = (scrollHeight - clientHeight) / 2;

    return bottomBorder < scrollTop;
  };
  const loadOptions = async (inputText: string) => {
    return await fetch(
      `https://wft-geo-db.p.rapidapi.com/v1/geo/cities?minPopulation=1000000&namePrefix=${inputText}`,
      options,
    )
      .then(response => response.json())
      .then(response => ({
        options: response.data.map((city: ICity) => ({
          label: `${city.name} ${city.countryCode}`,
          value: { latitude: city.latitude, longitude: city.longitude },
        })),
      }))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <AsyncPaginate
        placeholder="Search the city"
        debounceTimeout={600}
        value={search}
        // @ts-ignore
        onChange={handleChange}
        // @ts-ignore
        loadOptions={loadOptions}
      />
    </div>
  );
};
